run('C:\Users\Apratim\Documents\MATLAB\imofy\vlfeat-0.9.19-bin\vlfeat-0.9.19\toolbox\vl_setup.m');

trainDataSIFT=zeros(50000*64,128);                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
totalindex=1;
    %reason 80% train data 20% test data
    for index=1:50000
		disp(index);
        I=imageMatrixTrain(:,:,:,index);
        I=single(rgb2gray(I));
        for xcoord=2:4:30
            for ycoord=2:4:30
                fc=[xcoord;ycoord;2;0];
                [~,d]=vl_sift(I,'frames',fc);
                trainDataSIFT(totalindex,:)=d(:,1);
                totalindex=totalindex+1;
            end
        end
    end    
